(()=>{window.addEventListener("DOMContentLoaded",function(){let e=document.getElementById("translation_menu"),n=e.parentNode;n.addEventListener("touchend",function(){e.classList.contains("hidden")?e.classList.remove("hidden"):e.classList.add("hidden")}),n.addEventListener("mouseenter",function(){e.classList.remove("hidden")}),n.addEventListener("mouseleave",function(){e.classList.add("hidden")})});})();
